PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE mail(mail_id INTEGER PRIMARY KEY, sender_id INTEGER NOT NULL, receiver_id INTEGER NOT NULL, mail_subject TEXT, mail_body TEXT,  mail_date TEXT NOT NULL,mail_seen INTEGER DEFAULT 0 CHECK(mail_seen == 0 OR mail_seen == 1), sender_trashed INTEGER DEFAULT 0 CHECK(sender_trashed == 0 OR sender_trashed == 1), receiver_trashed INTEGER DEFAULT 0 CHECK(receiver_trashed == 0 OR receiver_trashed == 1), multiple_receivers INTEGER DEFAULT 0 CHECK(multiple_receivers == 0 OR multiple_receivers == 1), FOREIGN KEY(sender_id, receiver_id) REFERENCES user(user_id, user_id));
CREATE TABLE attachment(attachment_id INTEGER PRIMARY KEY, mail_id INTEGER NOT NULL, attachment_file TEXT NOT NULL, FOREIGN KEY(mail_id) REFERENCES mail(mail_id)ON DELETE CASCADE);
CREATE TABLE unauthorize(unauthorize_id INTEGER PRIMARY KEY, user_email TEXT NOT NULL, FOREIGN KEY(user_email) REFERENCES user(user_email), UNIQUE(user_email));
CREATE TABLE admin(admin_id INTEGER PRIMARY KEY, admin_email TEXT NOT NULL, admin_password TEXT NOT NULL, admin_phone TEXT NOT NULL, UNIQUE(admin_email, admin_phone));
CREATE TABLE waiting_list(waiting_id INTEGER PRIMARY KEY, user_name TEXT NOT NULL, user_email TEXT NOT NULL, user_password TEXT NOT NULL, user_gender TEXT NOT NULL, user_date_of_birth TEXT NOT NULL, user_city TEXT NOT NULL, user_country TEXT NOT NULL, user_contact TEXT NOT NULL, user_account_creation_date TEXT NOT NULL);
CREATE TABLE verfication_code(verfication_id INTEGER PRIMARY KEY, user_email TEXT NOT NULL, verfication_code INTEGER NOT NULL, birth_date TEXT NOT NULL);
CREATE TABLE user(user_id INTEGER PRIMARY KEY, user_name TEXT NOT NULL, user_email TEXT NOT NULL, user_password TEXT  NOT NULL, user_gender TEXT NOT NULL, user_date_of_birth TEXT NOT NULL, user_city TEXT NOT NULL, user_country TEXT NOT NULL, user_contact INTEGER, user_account_creation_date TEXT NOT NULL, user_active INTEGER DEFAULT 0 CHECK(user_active == 1 OR user_active == 0), visits INTEGER DEFAULT 0, UNIQUE(user_email));
COMMIT;
